try {
    const value = parseFloat(msg.payload);
    if (isNaN(value)) {
        node.error('Invalid decrypted temperature: ' + msg.payload);
        return null;
    }
    msg.payload = [{
        measurement: 'Temperatura',
        fields: {
            temperature: value
        },
        tags: {
            sensorID: 'sensor_Internal',
            location: 'internal_area'
        }
    }];
    return msg;
} catch (e) {
    node.error('Error formatting temperature: ' + e.message);
    return null;
}
